import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './styles.css';

const SchoolFoodComponent = () => {
    const [startDate, setStartDate] = useState(null);
    const [endDate, setEndDate] = useState(null);
    const [classValue, setClassValue] = useState('');
    const [nameValue, setNameValue] = useState('');
    const [surnameValue, setSurnameValue] = useState('');
    const [codeValue, setCodeValue] = useState('');
    const [hasContract, setHasContract] = useState(null);
    const [isPaid, setIsPaid] = useState(null);
    const [error, setError] = useState(null);
    const [updateStatus, setUpdateStatus] = useState(null);
    const [classes, setClasses] = useState([]);

    useEffect(() => {
        // Здесь вы можете выполнить запрос к вашему API для получения всех классов
        fetch('https://localhost:7045/SchoolFood/get-all-classes')
            .then(response => response.json())
            .then(data => setClasses(data))
            .catch(error => console.error('Error fetching classes:', error));
    }, []);

    const handleExcelData = async () => {
        try {
            if (startDate && endDate && startDate > endDate) {
                setError('Start Month must be greater than or equal to End Month.');
                return;
            }

            const requestBody = {
                StartDate: startDate,
                EndDate: endDate,
                Class: classValue,
                Name: nameValue,
                Surname: surnameValue,
                Code: codeValue,
                HasContract: hasContract !== null ? hasContract === "true" : null,
                IsPaid: isPaid !== null ? isPaid === "true" : null,
            };

            const response = await fetch('https://localhost:7045/SchoolFood/get-student-data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });


            console.log('Request:', requestBody);


            if (!response.ok) {
                throw new Error(`Request failed: ${response.url} - ${response.status} ${response.statusText}`);
            }

            // Получите бинарные данные в виде Blob
            const excelBlob = await response.blob();

            // Создайте URL для Blob
            const excelUrl = URL.createObjectURL(excelBlob);

            // Создайте скрытую ссылку для скачивания
            const link = document.createElement('a');
            link.href = excelUrl;
            link.download = 'StudentData.xlsx';
            link.style.display = 'none'; // Скрыть ссылку

            // Добавьте ссылку на страницу и симулируйте клик
            document.body.appendChild(link);
            link.click();

            // Удалите ссылку после завершения скачивания
            document.body.removeChild(link);
            URL.revokeObjectURL(excelUrl);

            alert('Data successfully downloaded');
        } catch (error) {
            console.error('Error downloading data:', error.message);
            alert('Error downloading data. Please check the console for more details.');
        }
    };

    const handleAtzimetSkolenu = async () => {
        try {
            const response = await fetch(`https://localhost:7045/SchoolFood/find/${codeValue}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error(`Request failed: ${response.url} - ${response.status} ${response.statusText}`);
            }

            const result = await response.text();
            setUpdateStatus(result);
            console.log(result); // Выведите результат запроса в консоль или обработайте его по вашему усмотрению.
            alert(result);

        } catch (error) {
            console.error('Error finding data:', error.message);
            alert('Error finding data(' + error.message + ')');
        }
    };

    const isWeekday = (date) => {
        const day = date.getDay();
        return day !== 0 && day !== 6; // Фильтруем субботу (0) и воскресенье (6)
    };


    return (
        <div className="slide">
            <div className="div">
                <button className="export-excel" onClick={handleExcelData}>
                    Importēt uz Excel
                </button>
                <button className="overlap" onClick={handleAtzimetSkolenu}>
                    Atzīmējiet studentu
                </button>
                <div className="text-wrapper-3">Datums:</div>
                <div className="text-wrapper-4">Klases:</div>
                <div className="text-wrapper-5">Ir samaksāts:</div>
                <div className="text-wrapper-6">Līgums:</div>
                <div className="text-wrapper-7">Vārds:</div>
                <div className="text-wrapper-8">Uzvārds:</div>
                <div className="text-wrapper-9">Kods:</div>
                <div className="overlap-2">
                    <input
                        className="v-rds-input"
                        type="number"
                        value={codeValue}
                        onChange={(e) => setCodeValue(e.target.value)}
                    />
                </div>
                <div className="v-rds-wrapper">
                    <input
                        className="v-rds-input"
                        type="text"
                        value={nameValue}
                        onChange={(e) => setNameValue(e.target.value)}
                    />
                </div>
                <div className="overlap-3">
                    <input
                        className="v-rds-input"
                        type="text"
                        value={surnameValue}
                        onChange={(e) => setSurnameValue(e.target.value)}
                    />
                </div>
                <select
                    className="div-wrapper"
                    onChange={(e) => setIsPaid(e.target.value === 'null' ? null : e.target.value === 'true')}
                    value={isPaid === null ? 'null' : isPaid ? 'true' : 'false'}
                >
                    <option value="null">Visi</option>
                    <option value="true">Ir</option>
                    <option value="false">Nav</option>
                </select>
                <select
                    className="overlap-4"
                    onChange={(e) => setClassValue(e.target.value)}
                    value={classValue}
                >
                    <option value="">Visi</option>
                    {classes.map((classItem) => (
                        <option key={classItem} value={classItem}>
                            {classItem}
                        </option>
                    ))}
                </select>

                <select
                    className="overlap-5"
                    onChange={(e) => setHasContract(e.target.value === 'null' ? null : e.target.value === 'true')}
                    value={hasContract === null ? 'null' : hasContract ? 'true' : 'false'}
                >
                    <option value="null">Visi</option>
                    <option value="true">Ir</option>
                    <option value="false">Nav</option>
                </select>
                <div className="overlap-6">
                    <DatePicker
                        selected={endDate}
                        onChange={(date) => setEndDate(date)}
                        dateFormat="yyyy-MM-dd"
                        className="date-picker"
                        minDate={startDate}
                        filterDate={isWeekday} // Используем фильтр для исключения выходных дней
                    />
                </div>

                <div className="overlap-7">
                    <DatePicker
                        selected={startDate}
                        onChange={(date) => setStartDate(date)}
                        dateFormat="yyyy-MM-dd"
                        className="date-picker"
                        maxDate={endDate}
                        filterDate={isWeekday} // Используем фильтр для исключения выходных дней
                    />
                </div>
            </div>
        </div>
    );
};

export default SchoolFoodComponent;
