import React, { useState } from 'react';

const SchoolFoodComponent = () => {
    const [startMonth, setStartMonth] = useState(null);
    const [endMonth, setEndMonth] = useState(null);
    const [classValue, setClassValue] = useState('');
    const [nameValue, setNameValue] = useState('');
    const [surnameValue, setSurnameValue] = useState('');
    const [codeValue, setCodeValue] = useState('');
    const [hasContract, setHasContract] = useState(false);
    const [isPaid, setIsPaid] = useState(false);
    const [updateStatus, setUpdateStatus] = useState(null);

    const handleUpdateData = async () => {
        try {
            const response = await fetch('https://localhost:7045/SchoolFood/update-data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    StartDate: startMonth,
                    EndDate: endMonth,
                    Class: classValue,
                    Name: nameValue,
                    Surname: surnameValue,
                    Code: codeValue,
                    HasContract: hasContract,
                    IsPaid: isPaid,
                }),
            });

            const data = await response.json();

            if (data.success) {
                setUpdateStatus('Data successfully updated');
            } else {
                setUpdateStatus(data.message);
            }
        } catch (error) {
            console.error('Error updating data:', error);
            setUpdateStatus('Error updating data');
        }
    };

    return (
        <div>
            <h1>School Food Tracker</h1>
            <label>
                Start Month:
                <input type="number" value={startMonth} onChange={(e) => setStartMonth(e.target.value)} />
            </label>
            <label>
                End Month:
                <input type="number" value={endMonth} onChange={(e) => setEndMonth(e.target.value)} />
            </label>
            <label>
                Class:
                <input type="text" value={classValue} onChange={(e) => setClassValue(e.target.value)} />
            </label>
            <label>
                Name:
                <input type="text" value={nameValue} onChange={(e) => setNameValue(e.target.value)} />
            </label>
            <label>
                Surname:
                <input type="text" value={surnameValue} onChange={(e) => setSurnameValue(e.target.value)} />
            </label>
            <label>
                Code:
                <input type="text" value={codeValue} onChange={(e) => setCodeValue(e.target.value)} />
            </label>
            <label>
                Has Contract:
                <input type="checkbox" checked={hasContract} onChange={() => setHasContract(!hasContract)} />
            </label>
            <label>
                Is Paid:
                <input type="checkbox" checked={isPaid} onChange={() => setIsPaid(!isPaid)} />
            </label>
            <button onClick={handleUpdateData}>Update Data</button>
            {updateStatus && <p>{updateStatus}</p>}
        </div>
    );
};

export default SchoolFoodComponent;
