import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
    const [updateMessage, setUpdateMessage] = useState('');
    const [studentCode, setStudentCode] = useState('');

    const handleFindStudent = async () => {
        try {
            // ��������� NODE_TLS_REJECT_UNAUTHORIZED � ������ ����������
            if (process.env.NODE_ENV === 'development') {
                process.env.NODE_TLS_REJECT_UNAUTHORIZED = '0';
            }

            const apiUrl = `https://localhost:7045/schoolfood/find/${studentCode}`;
            const response = await axios.get(apiUrl);
            setUpdateMessage(`Student Found. Response Code: ${response.status}`);
        } catch (error) {
            console.error('Error finding student:', error);
            setUpdateMessage(`Error finding student: ${error.message}`);
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <button onClick={handleFindStudent}>Find Student</button>
                <input
                    type="text"
                    value={studentCode}
                    onChange={(e) => setStudentCode(e.target.value)}
                    placeholder="Enter student code"
                />
                <p>{updateMessage}</p>
            </header>
        </div>
    );
}

export default App;
